module.exports = function (app, fs) {

    app.get('/', function (req, res) {
        fs.readFile('./data/index.html', function (err, html) {
            if (err) {
                throw err;
            }
            else {
                res.writeHeader(200, {"Content-Type": "text/html"});
                res.write(html);
                res.end();
            }
        });
    });

};
